sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
 if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
      return;
    }

   //Type appropriate comment here, and begin script below
   var usrEmail = new GlideAjax('needitUtils');
	usrEmail.addParam('sysparm_name', 'GetEmail');
	usrEmail.addParam('sysparm_LAPTOP', g_form.getValue('u_requested_for'));
	usrEmail.getXML(umpleFieldulDeEmail);

	//When response is back from server
	function umpleFieldulDeEmail(response) {
		var x = response.responseXML.documentElement.getAttribute("answer");
		g_form.setValue('u_requested_for_email',x ) ;
	}

   }
